import React, { Component } from 'react';
import Editor from './Editor';
import ScriptList from './ScriptList';
import './Scripts.css';

class Scripts extends Component {
    constructor(props) {
        super(props);
        this.state = {
            scripts: [
                {
                    name: 'Script 1',
                    value: 'example value 1',
                },
                {
                    name: 'Script 2',
                    value: 'example value 2',
                },
                {
                    name: 'Script 3',
                    value: 'example value 3',
                },
                {
                    name: 'Script 4',
                    value: 'example value 4',
                },
                {
                    name: 'Script 5',
                    value: 'example value 5',
                },
                {
                    name: 'Script 6',
                    value: 'example value 6',
                },
            ],
            editorValue: '',
        };
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(e) {
        let nodes = Array.prototype.slice.call(e.currentTarget.children);
        let index = nodes.indexOf(e.target);
        this.setState({ editorValue: this.state.scripts[index].value });
    }

    render() {
        return (
            <div className='scripts-page-container'>
                <Editor
                    className='editor'
                    editorValue={this.state.editorValue}
                />
                <ScriptList
                    className='script-list'
                    scripts={this.state.scripts}
                    onScriptSelect={this.handleClick}
                />
            </div>
        );
    }
}

export default Scripts;
