import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link, Switch } from 'react-router-dom';
import Login from './components/Login';
import Home from './components/Home';
import Scripts from './components/Scripts';
import Data from './components/Data';
import Charts from './components/Charts';
import Signup from './components/Signup';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import useToken from './useToken';

function App() {
    // call usestate and set return values to token and setToken
    // const [token,setToken] = useState();

    const { token, setToken } = useToken();
    const [showLogin, setShowLogin] = useState(true);

    // Pass the setToken function to the Login component
    if (!token) {
        // If state of showLogin is true, show original login page
        // If it is false show sign up page.
        // Sign up and sign in links set state
        if (showLogin) {
            return (
                <Login setToken={setToken} handleSignUp={handleSignUpClick} />
            );
        } else {
            return <Signup handleSignIn={handleSignInClick} />;
        }
    }

    function handleSignInClick(e) {
        e.preventDefault();
        setShowLogin(true);
    }

    function handleSignUpClick(e) {
        e.preventDefault();
        setShowLogin(false);
    }

    return (
        <Router>
            <main>
                <header>
                    <div className='header-container'>
                        <a
                            href='//www.ncsu.edu/'
                            rel='noreferrer'
                            target='_blank'
                        >
                            <img
                                src='https://ncsu-las.org/wp-content/themes/awi/img/logo-ncsu.png'
                                srcSet='https://ncsu-las.org/wp-content/themes/awi/img/logo-ncsu.png 1x, https://ncsu-las.org/wp-content/themes/awi/img/logo-ncsu@2x.png 2x'
                                target='_blank'
                                className='logo'
                                alt='NC State University'
                                title='NC State University'
                                width='105'
                                height='50'
                            />
                        </a>
                        <a
                            href='https://ncsu-las.org'
                            rel='noreferrer'
                            target='_blank'
                        >
                            <img
                                src='https://ncsu-las.org/wp-content/themes/awi/img/logo-las.png'
                                srcSet='https://ncsu-las.org/wp-content/themes/awi/img/logo-las.png 1x, https://ncsu-las.org/wp-content/themes/awi/img/logo-las@2x.png 2x'
                                target='_blank'
                                className='logo'
                                alt='Laboratory for Analytic Sciences'
                                title='Laboratory for Analytic Sciences'
                                width='215'
                                height='50'
                            />
                        </a>
                        <div className='nav'>
                            <Link to='/' className='menu-item'>
                                Home
                            </Link>
                            <Link to='/scripts' className='menu-item'>
                                Scripts
                            </Link>
                            <Link to='/data' className='menu-item'>
                                Data
                            </Link>
                            <Link to='/charts' className='menu-item'>
                                Charts
                            </Link>
                            <Link to='/help' className='menu-item'>
                                Help
                            </Link>

                            {/* <div className='username'>{token.username}</div> */}
                        </div>
                    </div>
                </header>
                <Switch>
                    <Route path='/' exact component={Home} />
                    <Route path='/scripts' component={Scripts} />
                    <Route path='/data' component={Data} />
                    <Route path='/charts' component={Charts} />
                    <Route path='/signup' component={Signup} />
                </Switch>
            </main>
        </Router>
    );
}

export default App;
