import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import PropTypes from 'prop-types';
import './Login.css';

async function loginUser(credentials) {
    return fetch('http://localhost:8080/login', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(credentials),
    }).then((data) => data.json());
}

export default function Login({ setToken, handleSignUp }) {
    const [username, setUserName] = useState();
    const [password, setPassword] = useState();

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = await loginUser({
            username,
            password,
        });
        setToken(token);
    };

    return (
        <div>
            <div className='container'>
                <a href='//www.ncsu.edu/' rel='noreferrer' target='_blank'>
                    <img
                        src='https://ncsu-las.org/wp-content/themes/awi/img/logo-ncsu.png'
                        srcSet='https://ncsu-las.org/wp-content/themes/awi/img/logo-ncsu.png 1x, https://ncsu-las.org/wp-content/themes/awi/img/logo-ncsu@2x.png 2x'
                        target='_blank'
                        className='logo'
                        alt='NC State University'
                        title='NC State University'
                        width='105'
                        height='50'
                    />
                </a>
                <a href='https://ncsu-las.org' rel='noreferrer' target='_blank'>
                    <img
                        src='https://ncsu-las.org/wp-content/themes/awi/img/logo-las.png'
                        srcSet='https://ncsu-las.org/wp-content/themes/awi/img/logo-las.png 1x, https://ncsu-las.org/wp-content/themes/awi/img/logo-las@2x.png 2x'
                        target='_blank'
                        className='logo'
                        alt='Laboratory for Analytic Sciences'
                        title='Laboratory for Analytic Sciences'
                        width='215'
                        height='50'
                    />
                </a>
            </div>

            <div className='Login'>
                <Form onSubmit={handleSubmit}>
                    <Form.Group
                        size='lg'
                        controlId='username'
                        placeholder='Enter username'
                    >
                        <Form.Label>Username</Form.Label>
                        <Form.Control
                            autoFocus
                            value={username}
                            onChange={(e) => setUserName(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group
                        size='lg'
                        controlId='password'
                        placeholder='Enter password'
                    >
                        <Form.Label>Password</Form.Label>
                        <Form.Control
                            type='password'
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </Form.Group>
                    <div className='form-group'>
                        <div className='custom-control custom-checkbox'>
                            <input
                                type='checkbox'
                                className='custom-control-input'
                                id='customCheck1'
                            />
                            <label
                                className='custom-control-label'
                                htmlFor='customCheck1'
                            >
                                Remember me
                            </label>
                        </div>
                    </div>
                    <Button block size='lg' type='submit'>
                        Login
                    </Button>
                    <p className='forgot-password text-right'>
                        <span className='login-span'>Forgot password?</span>
                    </p>

                    <p className='forgot-password text-right'>
                        New to the System?{' '}
                        <span className='login-span' onClick={handleSignUp}>
                            Sign Up
                        </span>
                    </p>
                </Form>
            </div>
        </div>
    );
}

Login.propTypes = {
    setToken: PropTypes.func.isRequired,
};
