import React, { useState } from 'react';
import AceEditor from 'react-ace';
import './Editor.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

import 'ace-builds/src-noconflict/mode-python';
import 'ace-builds/src-noconflict/theme-solarized_dark';
import 'ace-builds/src-noconflict/theme-solarized_light';
import 'ace-builds/src-noconflict/theme-monokai';
import 'ace-builds/src-noconflict/theme-tomorrow';
import 'ace-builds/src-noconflict/ext-language_tools';

function Editor() {
    const [editorValue, setEditorValue] = useState('');
    const [theme, setTheme] = useState('solarized_dark');
    const [saveButtonState, setSaveButtonState] = useState(false);
    const [scheduleButtonState, setScheduleButtonState] = useState(false);

    function changeTheme(e) {
        setTheme(e.target.value);
    }

    function onChange(newValue) {
        setEditorValue(newValue);
    }

    function saveScript() {
        console.log('save');
    }

    function scheduleScript() {
        console.log('schedule');
    }

    return (
        <div className='editor-container'>
            <AceEditor
                className='ace-editor'
                mode='python'
                onChange={onChange}
                theme={theme}
                name='Script Editor'
                placeholder='Enter Script'
                fontSize={14}
                showPrintMargin={true}
                showGutter={true}
                highlightActiveLine={true}
                setOptions={{
                    enableBasicAutocompletion: true,
                    enableLiveAutocompletion: true,
                    showLineNumbers: true,
                    tabSize: 4,
                }}
            />
            <div className='options-container'>
                <Form className='theme-form'>
                    <Form.Group controlId='selectTheme'>
                        <Form.Label className='theme-form-title'>
                            Select Theme
                        </Form.Label>
                        <Form.Control as='select' custom onChange={changeTheme}>
                            <option value='solarized_dark'>
                                Solarized Dark
                            </option>
                            <option value='solarized_light'>
                                Solarized Light
                            </option>
                            <option value='monokai'>Monokai</option>
                            <option value='tomorrow'>Tomorrow</option>
                        </Form.Control>
                    </Form.Group>
                </Form>
                <Button
                    className='save-button'
                    variant='outline-primary'
                    disabled={saveButtonState}
                    onClick={saveScript}
                >
                    Save
                </Button>
                <Button
                    className='schedule-button'
                    variant='outline-primary'
                    disabled={scheduleButtonState}
                    onClick={scheduleScript}
                >
                    Schedule
                </Button>
            </div>
        </div>
    );
}

export default Editor;
