import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import './Signup.css';

export default function Signup({ setToken, handleSignIn }) {
    const [firstname, setFirstName] = useState();
    const [lastname, setLastName] = useState();
    const [email, setEmail] = useState();
    const [username, setUserName] = useState();
    const [password, setPassword] = useState();

    const handleSubmit = async (e) => {
        e.preventDefault();
        // const token = await loginUser({
        //     username,
        //     password
        // });
        // setToken(token);
    };

    return (
        <div>
            <div className='container'>
                <a href='//www.ncsu.edu/' rel='noreferrer' target='_blank'>
                    <img
                        src='https://ncsu-las.org/wp-content/themes/awi/img/logo-ncsu.png'
                        srcSet='https://ncsu-las.org/wp-content/themes/awi/img/logo-ncsu.png 1x, https://ncsu-las.org/wp-content/themes/awi/img/logo-ncsu@2x.png 2x'
                        target='_blank'
                        className='logo'
                        alt='NC State University'
                        title='NC State University'
                        width='105'
                        height='50'
                    />
                </a>
                <a href='https://ncsu-las.org' rel='noreferrer' target='_blank'>
                    <img
                        src='https://ncsu-las.org/wp-content/themes/awi/img/logo-las.png'
                        srcSet='https://ncsu-las.org/wp-content/themes/awi/img/logo-las.png 1x, https://ncsu-las.org/wp-content/themes/awi/img/logo-las@2x.png 2x'
                        target='_blank'
                        className='logo'
                        alt='Laboratory for Analytic Sciences'
                        title='Laboratory for Analytic Sciences'
                        width='215'
                        height='50'
                    />
                </a>
            </div>

            <div className='Signup'>
                <Form onSubmit={handleSubmit}>
                    <Form.Group
                        size='lg'
                        controlId='firstname'
                        placeholder='Enter first name'
                    >
                        <Form.Label>First Name</Form.Label>
                        <Form.Control
                            autoFocus
                            value={firstname}
                            onChange={(e) => setFirstName(e.target.value)}
                        />
                    </Form.Group>

                    <Form.Group
                        size='lg'
                        controlId='lastname'
                        placeholder='Enter last name'
                    >
                        <Form.Label>Last Name</Form.Label>
                        <Form.Control
                            autoFocus
                            value={lastname}
                            onChange={(e) => setLastName(e.target.value)}
                        />
                    </Form.Group>

                    <Form.Group
                        size='lg'
                        controlId='email'
                        placeholder='Enter email'
                    >
                        <Form.Label>Email</Form.Label>
                        <Form.Control
                            autoFocus
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
                    </Form.Group>

                    <Form.Group
                        size='lg'
                        controlId='username'
                        placeholder='Enter username'
                    >
                        <Form.Label>Username</Form.Label>
                        <Form.Control
                            autoFocus
                            value={username}
                            onChange={(e) => setUserName(e.target.value)}
                        />
                    </Form.Group>

                    <Form.Group
                        size='lg'
                        controlId='password'
                        placeholder='Enter password'
                    >
                        <Form.Label>Password</Form.Label>
                        <Form.Control
                            type='password'
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </Form.Group>

                    <Button block size='lg' type='signup'>
                        Sign Up
                    </Button>

                    <p className='forgot-password text-right'>
                        Already Registered?{' '}
                        <span className='login-span' onClick={handleSignIn}>
                            Sign In
                        </span>
                    </p>
                </Form>
            </div>
        </div>
    );
}
