import React, { useState } from 'react';
import Grid from './Grid';
import './Data.css';
import Button from 'react-bootstrap/Button';
import ScriptList from './ScriptList';

function Data() {
    // GET request to fetch script list
    // temporary scripts array until api is ready
    let scripts = [
        {
            id: 'users',
            name: 'Script 1',
            value: 'example value 1',
        },
        {
            id: 'posts',
            name: 'Script 2',
            value: 'example value 2',
        },
        {
            id: 'comments',
            name: 'Script 3',
            value: 'example value 3',
        },
        {
            id: 'todos',
            name: 'Script 4',
            value: 'example value 4',
        },
        {
            id: 'albums',
            name: 'Script 5',
            value: 'example value 5',
        },
        {
            id: 'photos',
            name: 'Script 6',
            value: 'example value 6',
        },
    ];

    // Default selected script is index 0 in array
    const [selectedScript, setSelectedScript] = useState(scripts[0]);
    const [activeIndex, setActiveIndex] = useState(0);

    // On click of script list, set state to currently selected script
    function handleClick(e) {
        let nodes = Array.prototype.slice.call(e.currentTarget.children);
        let index = nodes.indexOf(e.target);
        let currentScript = scripts[index];
        setSelectedScript(currentScript);
        setActiveIndex(index);
    }

    function renderGrid(selectedScript) {
        return <Grid className='grid' script={selectedScript} />;
    }

    return (
        <div className='data-view-container'>
            <div className='grid-and-button-container'>
                {renderGrid(selectedScript)}
                <Button className='export-button' variant='outline-primary'>
                    Export Data
                </Button>
            </div>
            <div className='script-list'>
                <ScriptList
                    onScriptSelect={handleClick}
                    scriptList={scripts}
                    activeIndex={activeIndex}
                />
            </div>
        </div>
    );
}

export default Data;
